// frontend/src/App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Note from './components/note';
import { auth } from './components/firebase';
import GoogleSignIn from './components/GoogleSignIn';
import SignOut from './components/SignOut';

const App = () => {
  const [notes, setNotes] = useState([]);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [user, setUser] = useState(null);

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      setUser(user);
    });
    return () => unsubscribe();
  }, []);

  // Move the fetchNotes function outside useEffect to include it in the dependency array
  const fetchNotes = async () => {
    try {
      if (user) {
        const idToken = await auth.currentUser.getIdToken(); // Get Firebase ID token
        const res = await axios.get('http://localhost:5001/notes', {
          headers: {
            Authorization: `Bearer ${idToken}` // Pass Firebase ID token as the Authorization header
          }
        });
        setNotes(res.data);
      }
    } catch (error) {
      console.error('Error fetching notes:', error);
    }
  };

  // Include fetchNotes in the dependency array
  useEffect(() => {
    fetchNotes();
  }, [user, fetchNotes]);

  const addNote = async (e) => {
    e.preventDefault();
    try {
      const user = auth.currentUser;
      const userId = user ? user.uid : null;
      const res = await axios.post('http://localhost:5001/notes', { title, content, userId });
      setNotes([...notes, res.data]);
      setTitle('');
      setContent('');
    } catch (error) {
      console.error('Error adding note:', error);
    }
  };

  const deleteNote = async (id) => {
    try {
      await axios.delete(`http://localhost:5001/notes/${id}`);
      setNotes(notes.filter((note) => note._id !== id));
    } catch (error) {
      console.error('Error deleting note:', error);
    }
  };

  const updateNote = async (id, updatedTitle, updatedContent) => {
    try {
      await axios.patch(`http://localhost:5001/notes/${id}`, {
        title: updatedTitle,
        content: updatedContent
      });
      const updatedNotes = notes.map((note) => note._id === id
        ? { ...note, title: updatedTitle, content: updatedContent }
        : note
      );
      setNotes(updatedNotes);
    } catch (error) {
      console.error('Error updating note:', error);
    }
  };

  return (
    <div>
      <h1 className="bg-yellow-400 w-screen text-xl font-medium py-4 mx-auto text-center">Notes Keeper</h1>
      {user ? (
        <div>
          <SignOut />
          <form onSubmit={addNote} className="py-2 shadow-xl rounded-lg px-5 w-1/3 mx-auto text-left mt-10">
            <input
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              className="block shadow w-full mx-auto px-2 py-2 rounded-lg"
              type="text"
            />
            <textarea
              value={content}
              onChange={(e) => setContent(e.target.value)}
              className="block shadow w-full mx-auto my-2 px-2 py-4 rounded-lg"
              type="text"
            />
            <button type="submit" className="bg-yellow-400 text-2xl px-2 rounded py-1">
              Add Note
            </button>
          </form>
          {notes && notes.length > 0 && (
            <div className="grid grid-cols-4 gap-4 py-2">
              {notes.map((note) => (
                <Note
                  key={note._id}
                  id={note._id}
                  title={note.title}
                  content={note.content}
                  delete={() => deleteNote(note._id)}
                  updateNote={updateNote}
                />
              ))}
            </div>
          )}
        </div>
      ) : (
        <GoogleSignIn />
      )}
    </div>
  );
};

export default App;
